using System.Drawing;
using System.IO;

namespace ActivityMonitor.Services
{
    public static class IconService
    {
        private static readonly string Base64Icon = "";

        public static Icon GetApplicationIcon()
        {
            try
            {
                byte[] iconBytes = Convert.FromBase64String(Base64Icon);
                using var memoryStream = new MemoryStream(iconBytes);
                return new Icon(memoryStream);
            }
            catch (Exception ex)
            {
                System.Diagnostics.Debug.WriteLine($"从Base64加载图标失败: {ex.Message}");
                return SystemIcons.Application;
            }
        }
    }
}